import './App.css';
import './styles.css';
import Navbar from './Navbar';
import Home from './Pages/Home';
import About from './Pages/About';
import Pricing from './Pages/Pricing';

function App() {
  let Component = Home;
  let cp = <Home></Home>;

  switch (window.location.pathname) {
    case "/":
      Component = Home;
      cp = <Home></Home>;
      break;
    case "/pricing":
      Component = Pricing;
      cp = <Pricing></Pricing>;
      break;
    case "/about":
      Component = About;
      cp = <About></About>;
      break;
  }

  return (
    <>
      <Navbar></Navbar>
      <div className='container'>
        <Component></Component>
        {cp}
      </div>
    </>
  );
}

export default App;
